const {express, connection, mysql, app} = require('./connexion');

function validateNom(){
    var nomRegExp = new RegExp('^[a-zA-Z-\']{3,}$', 'g');
    var testNom = nomRegExp.test();
    if (testNom) {
        console.log('Nom valide');
    }else{
        console.log('Nom invalide');
    }
    return testNom;
}
function apostrophe(str) {
    if (str.match("'")) {
        var str = str.split("'").join("''");
        console.log(str);
        // if (str) {
        //     console.log('ok');
        // }
    }
    return str;
}
function nombre(nb){
    if (Number.isInteger(nb)) {
        console.log(nb);
        return nb;
    }else {
        console.log('Ce n\'est pas un nombre');
    }
}
function newDate(mydate){
    new Date(mydate);
    console.log(mydate);
    console.log(mydate.toISOString);
    if (mydate) {
        console.log('Date valide');
        return mydate;
    } else {
        console.log('Pas format date');
    }
}
    
